  <JS_VALIDAR type="javascript" meta=""><![CDATA[getField("BTN_ENVIAR").form.onsubmit = function(){

  var direccion = /^([áéíúóñÑa-zA-Z0-9\.\-\#\s]+)$/;
  var sim_num = /^([0-9 \-]+)$/;
      
  var sol1 = getField("DRP_GEN").selectedIndex;
  var sol2 = getField("DATE_NACIMIENTO").value;
  var sol3 = getField("TXT_DIRECCION").value;
  var sol4 = getField("TXT_TEL_FIJO").value;


    
  
  if(sol1==0){
  
    alert("Por favor seleccione una opción");
      return false;
  }
  
  if(sol2==0){
  
    alert("Por favor ingrese su fecha de nacimiento");
      return false;
  }
  
  if(sol3==0){
  
    alert("Por favor ingrese su dirección de residencia");
      return false;
  }
  
  if(!direccion.test(sol3) ){
      alert("La dirección solo permite letras y los simbolos . , - , #");
        return false;
    } 

  if(!sim_num.test(sol4) ){
      alert("El teléfono solo permite números.");
        return false;
  }  

  if(sol4==0){  
    var res = confirm("Se recomienda ingresar un numero de contacto, si desea ingresar un numero de click en cancelar, caso contrario continue con aceptar");              
    if (res == true) {
         return true;
    } else {
          return false;
    }
  }

  
  
}
  
  
